INSERT INTO `dtb_news` (`id`, `user_id`, `title`, `body`, `image`, `youtube`, `notice`, `status`, `noticed`, `send`, `lang`, `created`, `modified`) VALUES (20, 20, 'Trump details domest', 'Republican presidential candidate Donald Trump on Sunday released details on his domestic and foreign policy plans, including perhaps using U.S. ground troops to fight Islamic State militants, after weeks of criticism that his campaign has been short on specifics.\r\n\r\nTrump entered the race in mid-June, making him among the last in the 2016 presidential race to say exactly what he would do, if elected, about such issues as illegal immigration, Islamic extremist groups and continued funding for Planned Parenthood.  \r\n\r\nThe billionaire New York real estate tycoon and former reality TV star announced his plans on his website and on NBC’s “Meet the Press.”', '86b8c97fe0', '', 1, 1, '0000-0-0 00:00:00', '0000-0-0 00:00:00', 'en', '2015-8-17 11:27:14', '2015-8-17 11:27:14');
